mapper.update:
UPDATE GY_MEMBER
SET
	PASSWORD = #{password}
	, MEMBER_NAME = #{member_name}
	, COM_NAME = #{com_name}
	, COM_CEO = #{com_ceo}
	, EMAIL = #{email}
	, PHONE = #{phone}
	, MOBILE = #{mobile}
	, AREA = #{area}
	, YEAR = #{year}
WHERE
	MEMBER_ID = #{member_id}

mapper.select:
SELECT SUBSTR(A.REPAIR_NO,1,6)||'-'||SUBSTR(A.REPAIR_NO,9,2) AS "접수번호" , F_GY_DATA_CODE_NM('14', REPAIR_CD)||'/'||F_GY_DATA_CODE_NM('13', LIGHT_GUBUN) AS "민원종류" , A.LIGHT_NO AS "관리번호" , A.NOTICE_NAME AS "신고인" , CASE WHEN INFORM_METHOD = '01' THEN MOBILE WHEN INFORM_METHOD = '02' THEN EMAIL WHEN INFORM_METHOD = '03' THEN PHONE WHEN INFORM_METHOD = '04' THEN '' END AS "전화번호" , TO_CHAR(A.NOTICE_DATE, 'YYYY.MM.DD') AS "접수일" , TO_CHAR(TO_DATE(B.REPAIR_DATE, 'YYYYMMDD'), 'YYYY.MM.DD') AS "보수일" , F_GY_DATA_CODE_NM('03', PROGRESS_STATUS) AS "처리상황" FROM GY_LIGHT_REPAIR A, GY_LIGHT_REPAIR_PART B WHERE A.REPAIR_NO = B.REPAIR_NO AND B.COMPANY_ID = #{member_id} AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} and #{eDate}
AND A.REPAIR_CD = #{repair_cd}
AND A.REPAIR_CD = #{sch_repair_cd}
AND A.LIGHT_NO LIKE '%'|| #{sch_where3} ||'%'
AND A.NOTICE_NAME LIKE '%'|| #{sch_where3} ||'%'
AND A.PROGRESS_STATUS LIKE '%'|| #{sch_where2} ||'%'
AND A.DONG LIKE '%'|| #{sch_where2} ||'%'
AND A.LIGHT_NO LIKE '%'|| #{sch_where2} ||'%'
ORDER BY A.REPAIR_NO

mapper.update:
update gy_light_repair 
set
	progress_status = #{progress_status}
	, repair_gubun = #{repair_gubun}
	, repair_bigo = #{repair_bigo}	
	, repair_date = to_char(now(), 'yyyy-mm-dd')
WHERE
	repair_no = #{repair_no}

update gy_light_repair_part 
set
	repair_desc = #{repair_desc}
	, photo1 = #{photo1}
	, photo2 = #{photo2}
	, photo3 = #{photo3}
	, last_update = now()
	, repair_date = to_char(now(), 'yyyymmdd')
WHERE
	repair_no = #{repair_no}

UPDATE GY_LIGHT_PART 
SET
	INOUT_CNT = #{inout_cnt}
	, LAST_UPDATE = now()
WHERE
	SEQ_NO = #{seq_no}
	AND INOUT_DAY = #{inout_day}
	AND PART_CD = #{part_cd}
	AND INOUT_FLAG = '2'
	AND REPAIR_NO = #{repair_no}

mapper.delete:
DELETE FROM GY_LIGHT_PART
WHERE
	SEQ_NO = #{seq_no}
	AND INOUT_DAY = #{inout_day}
	AND PART_CD = #{part_cd}
	AND INOUT_FLAG = '2'
