mapper.select:

	SELECT COUNT(*)
	FROM GY_LIGHT_REPAIR A
	WHERE
		TO_CHAR(NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}

	
		AND A.LIGHT_GUBUN = #{light_gubun}	
	
	
		AND A.REPAIR_CD = #{repair_cd}	
	
	
		
			
				AND NOTICE_NAME LIKE '%'||#{keyword}||'%'
			
			
				AND LOCATION LIKE '%'||#{keyword}||'%'
			
			
				AND LIGHT_NO LIKE '%'||#{keyword}||'%'
			
			
				AND TROUBLE_CD = #{trouble_cd}
			
			
				AND PROGRESS_STATUS = #{progress_status}

SELECT T1.* FROM ( SELECT A.REPAIR_NO , F_GY_DATA_CODE_NM('14', A.REPAIR_CD)||'/'||F_GY_DATA_CODE_NM('13', A.LIGHT_GUBUN) AS LIGHT_GUBUN , A.LIGHT_NO , A.NOTICE_NAME , CASE WHEN A.inform_method = '03' THEN PHONE WHEN A.inform_method = '01' THEN MOBILE ELSE COALESCE(COALESCE(MOBILE, PHONE), '') END AS CONTACT , A.EMAIL , TO_CHAR(A.NOTICE_DATE, 'YYYY.MM.DD') AS NOTICE_DATE , COALESCE(TO_CHAR(TO_DATE(B.REPAIR_DATE, 'YYYYMMDD'), 'YYYY.MM.DD'), '') AS REPAIR_DATE , F_GY_DATA_CODE_NM('01', A.TROUBLE_CD) AS TROUBLE_NM , A.PROGRESS_STATUS , A.LOCATION FROM GY_LIGHT_REPAIR A LEFT OUTER JOIN GY_LIGHT_REPAIR_PART B ON A.REPAIR_NO = B.REPAIR_NO WHERE 1=1
AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}
AND A.LIGHT_GUBUN = #{light_gubun}
AND A.REPAIR_CD = #{repair_cd}
AND NOTICE_NAME LIKE '%'||#{keyword}||'%'
AND LOCATION LIKE '%'||#{keyword}||'%'
AND LIGHT_NO LIKE '%'||#{keyword}||'%'
AND TROUBLE_CD = #{trouble_cd}
AND PROGRESS_STATUS = #{progress_status}
ORDER BY A.REPAIR_NO DESC ) T1 LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE GY_LIGHT_REPAIR
SET
	PASSWORD = #{password}
	, NOTICE_NAME = #{notice_name}
	, LOCATION = #{address}
	, PHONE = #{phone}
	, MOBILE = #{mobile}
	, EMAIL = #{email}
	, TROUBLE_CD = #{trouble_cd} 
	, TROUBLE_DETAIL = #{trouble_detail}
	, INFORM_METHOD = #{inform_method}
	, MODIFY_DATE = NOW()
WHERE
	REPAIR_NO = #{repair_no}
	AND LIGHT_NO = #{light_no}
