mapper.select:

	SELECT COUNT(*)
	FROM GY_LIGHT_REPAIR A
	WHERE
		TO_CHAR(NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}

	
		AND A.LIGHT_GUBUN = #{light_gubun}	
	
	
		AND A.REPAIR_CD = #{repair_cd}	
	
	
		
			
				AND NOTICE_NAME LIKE '%'||#{keyword}||'%'
			
			
				AND LOCATION LIKE '%'||#{keyword}||'%'
			
			
				AND LIGHT_NO LIKE '%'||#{keyword}||'%'
			
			
				AND TROUBLE_CD = #{trouble_cd}
			
			
				AND PROGRESS_STATUS = #{progress_status}

SELECT T1.* FROM ( SELECT REPAIR_NO , F_GY_DATA_CODE_NM('14', REPAIR_CD)||'/'||F_GY_DATA_CODE_NM('13', LIGHT_GUBUN) AS LIGHT_GUBUN , LIGHT_NO , NOTICE_NAME , CASE WHEN inform_method = '03' THEN PHONE WHEN inform_method = '01' THEN MOBILE WHEN inform_method = '04' THEN '' END AS CONTACT , EMAIL , TO_CHAR(NOTICE_DATE, 'YYYY.MM.DD') AS NOTICE_DATE , TO_CHAR(MODIFY_DATE, 'YYYY.MM.DD') AS MODIFY_DATE , TO_CHAR(TO_DATE(REPAIR_DATE, 'YYYY.MM.DD'), 'YYYY.MM.DD') AS REPAIR_DATE , F_GY_DATA_CODE_NM('01', TROUBLE_CD) AS TROUBLE_NM , PROGRESS_STATUS , LOCATION FROM GY_LIGHT_REPAIR A WHERE 1=1
AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}
AND A.LIGHT_GUBUN = #{light_gubun}
AND A.REPAIR_CD = #{repair_cd}
AND NOTICE_NAME LIKE '%'||#{keyword}||'%'
AND LOCATION LIKE '%'||#{keyword}||'%'
AND LIGHT_NO LIKE '%'||#{keyword}||'%'
AND TROUBLE_CD = #{trouble_cd}
AND PROGRESS_STATUS = #{progress_status}
ORDER BY A.REPAIR_NO DESC ) T1 LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE GY_LIGHT_REPAIR
SET
	PASSWORD = #{password}
	, NOTICE_NAME = #{notice_name}
	, LOCATION = #{address}
	, PHONE = #{phone}
	, MOBILE = #{mobile}
	, EMAIL = #{email}
	, TROUBLE_CD = #{trouble_cd} 
	, TROUBLE_DETAIL = #{trouble_detail}
	, INFORM_METHOD = #{inform_method}
WHERE
	REPAIR_NO = #{repair_no}
	AND LIGHT_NO = #{light_no}
