mapper.insert:

	
		SELECT COALESCE(MAX(DATA_CODE::NUMERIC), 0)+1
		FROM GY_MATERIAL
		WHERE YEAR    = #{sch_year}
	


INSERT INTO GY_MATERIAL
(
	YEAR
	, DATA_CODE
	, DATA_CODE_NAME
	, COMPANY_ID
	, STANDARD
	, DANGA
	, UNIT
	, REMARKS
	, last_update
)
VALUES
(
	#{sch_year}
	, #{data_code}
	, #{data_code_name}
	, #{company_id}
	, #{standard}
	, #{danga}
	, #{unit}
	, #{remarks}
	, now()
)

mapper.update:
UPDATE GY_MATERIAL
SET
	DATA_CODE_NAME = #{data_code_name}
	, COMPANY_ID = #{company_id}
	, STANDARD = #{standard}
	, DANGA = #{danga}
	, UNIT = #{unit}
	, REMARKS = #{remarks}
	, last_update = now()
WHERE 1=1
  AND YEAR = #{year}
  AND DATA_CODE = #{data_code}
  AND COMPANY_ID = #{company_id}

DELETE FROM GY_MATERIAL
WHERE YEAR = #{year}
  AND DATA_CODE = #{data_code} 
  AND COMPANY_ID = #{company_id}
