mapper.select:

	SELECT COUNT(*)
	FROM GY_LIGHT_REPAIR A
	WHERE
		TO_CHAR(NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}

	
		AND A.LIGHT_GUBUN = #{light_gubun}	
	
	
		AND A.REPAIR_CD = #{repair_cd}

SELECT T1.* FROM ( SELECT REPAIR_NO , F_GY_DATA_CODE_NM('14', REPAIR_CD)||'/'||F_GY_DATA_CODE_NM('13', LIGHT_GUBUN) AS LIGHT_GUBUN , LIGHT_NO , NOTICE_NAME , CASE WHEN inform_method = '03' THEN PHONE WHEN inform_method = '01' THEN MOBILE WHEN inform_method = '02' THEN EMAIL WHEN inform_method = '04' THEN '' END AS CONTACT , TO_CHAR(NOTICE_DATE, 'YYYY.MM.DD') AS NOTICE_DATE , TO_CHAR(MODIFY_DATE, 'YYYY.MM.DD') AS MODIFY_DATE , TO_CHAR(TO_DATE(REPAIR_DATE, 'YYYY.MM.DD'), 'YYYY.MM.DD') AS REPAIR_DATE , F_GY_DATA_CODE_NM('01', TROUBLE_CD) AS TROUBLE_NM , PROGRESS_STATUS FROM GY_LIGHT_REPAIR A WHERE 1=1
AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}
AND A.LIGHT_GUBUN = #{light_gubun}
AND A.REPAIR_CD = #{repair_cd}
ORDER BY A.REPAIR_NO DESC ) T1 LIMIT #{limit} OFFSET #{offset}

SELECT REPAIR_NO AS "접수번호" , F_GY_DATA_CODE_NM('14', REPAIR_CD) AS "민원종류" , LIGHT_NO AS "관리번호" , NOTICE_NAME AS "신고인" , CASE WHEN inform_method = '03' THEN PHONE WHEN inform_method = '01' THEN MOBILE WHEN inform_method = '02' THEN EMAIL WHEN inform_method = '04' THEN '' END AS "전화번호" , TO_CHAR(NOTICE_DATE, 'YYYY.MM.DD') AS "접수일" , TO_CHAR(MODIFY_DATE, 'YYYY.MM.DD') AS "지시일" , TO_CHAR(TO_DATE(REPAIR_DATE, 'YYYY.MM.DD'), 'YYYY.MM.DD') AS "보수일" , F_GY_DATA_CODE_NM('01', TROUBLE_CD) AS TROUBLE_NM , F_GY_DATA_CODE_NM('03', PROGRESS_STATUS) AS "처리상황" FROM GY_LIGHT_REPAIR A WHERE TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}
AND A.REPAIR_CD = #{repair_cd}
ORDER BY A.LIGHT_NO
