mapper.select:
		
			SELECT COUNT(*)              
			  FROM GY_MATERIAL A              
			 WHERE A.YEAR = #{searchYear}			   
   			   AND (SELECT COALESCE(MAX(MEMBER_ID), '') AS MEMBER_ID
          			  FROM GY_MEMBER
         			WHERE A.COMPANY_ID = MEMBER_ID) = #{searchCom}

SELECT A.DATA_CODE AS PART_CD, A.DATA_CODE_NAME AS PART_NAME, A.YEAR, A.COMPANY_ID, (SELECT B.COM_NAME FROM GY_MEMBER B WHERE B.MEMBER_ID = A.COMPANY_ID) AS COMP_NM, CASE WHEN (SELECT SUM(INOUT_CNT) FROM GY_LIGHT_PART B WHERE B.YEAR = A.YEAR AND B.COMP_NM = A.COMPANY_ID AND PART_CD = DATA_CODE AND INOUT_FLAG = '1') IS NULL THEN 0 ELSE (SELECT SUM(INOUT_CNT) FROM GY_LIGHT_PART B WHERE B.YEAR = A.YEAR AND B.COMP_NM = A.COMPANY_ID AND PART_CD = DATA_CODE AND INOUT_FLAG = '1') END AS IN_CNT, CASE WHEN (SELECT SUM(INOUT_CNT) FROM GY_LIGHT_PART B WHERE B.YEAR = A.YEAR AND B.COMP_NM = A.COMPANY_ID AND PART_CD = DATA_CODE AND INOUT_FLAG = '2') IS NULL THEN 0 ELSE (SELECT SUM(INOUT_CNT) FROM GY_LIGHT_PART B WHERE B.YEAR = A.YEAR AND B.COMP_NM = A.COMPANY_ID AND PART_CD = DATA_CODE AND INOUT_FLAG = '2') END AS OUT_CNT , (SELECT COALESCE(MAX(TO_CHAR(LAST_UPDATE, 'YYYY-MM-DD')), '') AS LAST_UPDATE FROM GY_LIGHT_PART WHERE YEAR = A.YEAR AND COMP_NM = A.COMPANY_ID AND PART_CD = DATA_CODE AND INOUT_FLAG='1') AS LAST_UPDATE FROM GY_MATERIAL A WHERE A.YEAR = #{searchYear}
AND (SELECT COALESCE(MAX(MEMBER_ID), '') AS MEMBER_ID FROM GY_MEMBER WHERE A.COMPANY_ID = MEMBER_ID) = #{searchCom}
ORDER BY DATA_CODE ASC

			
			SELECT *, 
					(SELECT DATA_CODE_NAME 
					   FROM GY_MATERIAL 
					 WHERE PART_CD = DATA_CODE AND YEAR = YEAR
									 
								  
					 AND COMPANY_ID = #{searchCom}
			         			
		  					 
					) AS PART_NAME, 
					(SELECT MEMBER_NAME FROM GY_MEMBER WHERE MEMBER_ID = COMPANY_ID) AS COMPANY_NAME,
         			COALESCE(BIGO, '') AS BIGO1					
			FROM GY_LIGHT_PART 
			WHERE 1 = 1
						
						
			  AND PART_CD = #{searchPart}
			         			
						
			  AND YEAR = #{searchYear}
						   
							
			  AND COMP_NM = #{searchCom}
			
		 			   
			ORDER BY INOUT_DAY DESC, SEQ_NO DESC

mapper.insert:
INSERT INTO GY_LIGHT_PART ( 
	INOUT_DAY, PART_CD, INOUT_FLAG, INOUT_CNT, LAST_UPDATE, 
	BIGO, COMPANY_ID, COMPANY_ID1, YEAR, COMP_NM 
) VALUES ( 
	#{inout_day}, #{part_cd}, #{inout_flag}, CAST(#{inout_cnt} AS INTEGER), NOW(), #{bigo1}, #{company_id}, #{company_id1}, #{year}, #{company_id} 
)

mapper.update:
UPDATE GY_LIGHT_PART 
SET 
	 INOUT_DAY = #{inout_day}
	 , PART_CD = #{part_cd}
	 , INOUT_FLAG = #{inout_flag}
	 , INOUT_CNT = CAST(#{inout_cnt} AS INTEGER)  
	 , LAST_UPDATE = NOW()
	 , BIGO = #{bigo1}
	 , COMPANY_ID = #{company_id}
	 , COMPANY_ID1 = #{company_id1}
	 , YEAR = #{year}
	 , COMP_NM = #{company_id}
 WHERE SEQ_NO = CAST(#{seq_no} AS INTEGER)
