mapper.select:

	SELECT COUNT(*)
	FROM GY_LIGHT A
	WHERE
		1=1
		AND A.LIGHT_GUBUN <> '9'

	
		AND A.LIGHT_TYPE = #{lightType}	
	
	
		AND HJ_DONG_CD = #{searchArea}
	
	
		AND LIGHT_GUBUN = #{searchLampGubun}
	
	
		
			
				AND LIGHT_NO LIKE '%'||#{keyword}||'%'
			
			
				AND ADDRESS LIKE '%'||#{keyword}||'%'
			
			
				AND NEW_ADDRESS LIKE '%'||#{keyword}||'%'
			
			
				AND STAND_CD = #{stand_cd}
			
			
				AND LAMP2_CD = #{lamp2_cd}
			
			
				AND LAMP3_CD = #{lamp3_cd}
			
			
				AND KEPCO_CUST_NO LIKE '%'||#{keyword}||'%'

SELECT T1.* FROM ( SELECT A.*, F_GY_DATA_CODE_NM('05', LIGHT_GUBUN) AS LIGHT_GUBUN_NM, F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS HJ_DONG_NM, F_GY_DATA_CODE_NM('06', BJ_DONG_CD) AS BJ_DONG_NM, F_GY_DATA_CODE_NM('08', STAND_CD) AS STAND_NM, F_GY_DATA_CODE_NM('09', LAMP1_CD) AS LAMP1_NM, F_GY_DATA_CODE_NM('10', LAMP2_CD) AS LAMP2_NM, F_GY_DATA_CODE_NM('11', LAMP3_CD) AS LAMP3_NM, F_GY_DATA_CODE_NM('12', KEPCO_CD) AS KEPCO_NM, F_GY_DATA_CODE_NM('15', ONOFF_CD) AS ONOFF_NM, F_GY_DATA_CODE_NM('16', WORK_CD) AS WORK_NM, F_GY_DATA_CODE_NM('14', REPAIR_CD) AS REPAIR_NM, F_GY_DATA_CODE_NM('29', AUTO_JUM_TYPE1_CD) AS AUTO_JUM_TYPE1_NM FROM GY_LIGHT A WHERE 1=1 AND A.LIGHT_GUBUN <> '9'
AND A.LIGHT_TYPE = #{lightType}
AND HJ_DONG_CD = #{searchArea}
AND LIGHT_GUBUN = #{searchLampGubun}
AND LIGHT_NO LIKE '%'||#{keyword}||'%'
AND ADDRESS LIKE '%'||#{keyword}||'%'
AND NEW_ADDRESS LIKE '%'||#{keyword}||'%'
AND STAND_CD = #{stand_cd}
AND LAMP2_CD = #{lamp2_cd}
AND LAMP3_CD = #{lamp3_cd}
AND KEPCO_CUST_NO LIKE '%'||#{keyword}||'%'
ORDER BY LIGHT_NO ) T1 LIMIT #{limit} OFFSET #{offset}

SELECT '' AS IDX , HJ_DONG_NM , COUNT(*) AS TOTAL_CNT , SUM(A.CNT_1) AS "CNT_1" , SUM(A.CNT_2) AS "CNT_2" , SUM(A.CNT_3) AS "CNT_3" FROM ( SELECT F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS HJ_DONG_NM , LIGHT_TYPE , HJ_DONG_CD , COALESCE(CASE WHEN LIGHT_GUBUN = '1' THEN 1 END, 0) AS CNT_1 , COALESCE(CASE WHEN LIGHT_GUBUN = '2' THEN 1 END, 0) AS CNT_2 , COALESCE(CASE WHEN LIGHT_GUBUN = '3' THEN 1 END, 0) AS CNT_3 FROM GY_LIGHT ) A WHERE 1=1
AND LIGHT_TYPE = #{light_type}
GROUP BY HJ_DONG_NM ORDER BY HJ_DONG_NM

SELECT '' AS IDX , HJ_DONG_NM , SUM(A.CNT_1) AS "CNT_1" , SUM(A.CNT_2) AS "CNT_2" , SUM(A.CNT_3) AS "CNT_3" , SUM(A.CNT_4) AS "CNT_4" , SUM(A.CNT_5) AS "CNT_5" , SUM(A.CNT_6) AS "CNT_6" , SUM(A.CNT_7) AS "CNT_7" , SUM(A.CNT_8) AS "CNT_8" , SUM(A.CNT_9) AS "CNT_9" , SUM(A.CNT_10) AS "CNT_10" , SUM(A.CNT_11) AS "CNT_11" , SUM(A.CNT_12) AS "CNT_12" , COUNT(*) AS TOTAL_CNT FROM ( SELECT F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS HJ_DONG_NM , LIGHT_TYPE , COALESCE((CASE WHEN STAND_CD = '01' THEN 1 END), 0) AS CNT_1 , COALESCE((CASE WHEN STAND_CD = '02' THEN 1 END), 0) AS CNT_2 , COALESCE((CASE WHEN STAND_CD = '03' THEN 1 END), 0) AS CNT_3 , COALESCE((CASE WHEN STAND_CD = '04' THEN 1 END), 0) AS CNT_4 , COALESCE((CASE WHEN STAND_CD = '05' THEN 1 END), 0) AS CNT_5 , COALESCE((CASE WHEN STAND_CD = '06' THEN 1 END), 0) AS CNT_6 , COALESCE((CASE WHEN STAND_CD = '07' THEN 1 END), 0) AS CNT_7 , COALESCE((CASE WHEN STAND_CD = '08' THEN 1 END), 0) AS CNT_8 , COALESCE((CASE WHEN STAND_CD = '09' THEN 1 END), 0) AS CNT_9 , COALESCE((CASE WHEN STAND_CD = '10' THEN 1 END), 0) AS CNT_10 , COALESCE((CASE WHEN STAND_CD = '11' THEN 1 END), 0) AS CNT_11 , COALESCE((CASE WHEN STAND_CD = '12' THEN 1 END), 0) AS CNT_12 FROM GY_LIGHT ) A WHERE 1=1
AND LIGHT_TYPE = #{light_type}
GROUP BY HJ_DONG_NM ORDER BY HJ_DONG_NM

SELECT '' AS IDX , HJ_DONG_NM , SUM(A.CNT_1) AS "CNT_1" , SUM(A.CNT_2) AS "CNT_2" , SUM(A.CNT_3) AS "CNT_3" , SUM(A.CNT_4) AS "CNT_4" , COUNT(*) AS TOTAL_CNT FROM ( SELECT F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS HJ_DONG_NM , LIGHT_TYPE , COALESCE((CASE WHEN STAND_CD = '01' THEN 1 END), 0) AS CNT_1 , COALESCE((CASE WHEN STAND_CD = '02' THEN 1 END), 0) AS CNT_2 , COALESCE((CASE WHEN STAND_CD = '03' THEN 1 END), 0) AS CNT_3 , COALESCE((CASE WHEN STAND_CD = '04' THEN 1 END), 0) AS CNT_4 FROM GY_LIGHT ) A WHERE 1=1
AND LIGHT_TYPE = #{light_type}
GROUP BY HJ_DONG_NM ORDER BY HJ_DONG_NM

SELECT '' AS IDX , HJ_DONG_NM , SUM(A.CNT_1) AS "CNT_1" , SUM(A.CNT_2) AS "CNT_2" , SUM(A.CNT_3) AS "CNT_3" , SUM(A.CNT_4) AS "CNT_4" , SUM(A.CNT_5) AS "CNT_5" , SUM(A.CNT_6) AS "CNT_6" , SUM(A.CNT_7) AS "CNT_7" , SUM(A.CNT_8) AS "CNT_8" , SUM(A.CNT_9) AS "CNT_9" , SUM(A.CNT_10) AS "CNT_10" , COUNT(*) AS TOTAL_CNT FROM ( SELECT F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS HJ_DONG_NM , LIGHT_TYPE , COALESCE((CASE WHEN STAND_CD = '01' THEN 1 END), 0) AS CNT_1 , COALESCE((CASE WHEN STAND_CD = '02' THEN 1 END), 0) AS CNT_2 , COALESCE((CASE WHEN STAND_CD = '03' THEN 1 END), 0) AS CNT_3 , COALESCE((CASE WHEN STAND_CD = '04' THEN 1 END), 0) AS CNT_4 , COALESCE((CASE WHEN STAND_CD = '05' THEN 1 END), 0) AS CNT_5 , COALESCE((CASE WHEN STAND_CD = '06' THEN 1 END), 0) AS CNT_6 , COALESCE((CASE WHEN STAND_CD = '07' THEN 1 END), 0) AS CNT_7 , COALESCE((CASE WHEN STAND_CD = '08' THEN 1 END), 0) AS CNT_8 , COALESCE((CASE WHEN STAND_CD = '09' THEN 1 END), 0) AS CNT_9 , COALESCE((CASE WHEN STAND_CD = '10' THEN 1 END), 0) AS CNT_10 FROM GY_LIGHT ) A WHERE 1=1
AND LIGHT_TYPE = #{light_type}
GROUP BY HJ_DONG_NM ORDER BY HJ_DONG_NM

SELECT A.REPAIR_NO , F_GY_DATA_CODE_NM('14', A.REPAIR_CD)||'/'||F_GY_DATA_CODE_NM('13', A.LIGHT_GUBUN) AS LIGHT_GUBUN , F_GY_DATA_CODE_NM('01', A.TROUBLE_CD) AS TROUBLE_NM , A.LIGHT_NO , A.LOCATION , A.NOTICE_NAME , CASE WHEN A.inform_method = '03' THEN A.PHONE WHEN A.inform_method = '01' THEN A.MOBILE WHEN A.inform_method = '02' THEN A.EMAIL WHEN A.inform_method = '04' THEN '' END AS CONTACT , F_GY_DATA_CODE_NM('03', A.PROGRESS_STATUS) AS PROGRESS_STATUS , COALESCE(C.MEMBER_NAME, '') AS MEMBER_NAME FROM GY_LIGHT_REPAIR A LEFT OUTER JOIN GY_LIGHT_REPAIR_PART B ON A.REPAIR_NO = B.REPAIR_NO LEFT OUTER JOIN GY_MEMBER C ON B.COMPANY_ID = C.MEMBER_ID WHERE 1=1
AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate} AND #{eDate}
AND A.LIGHT_GUBUN = #{light_gubun}
AND A.REPAIR_CD = #{repair_cd}
AND A.NOTICE_NAME LIKE '%'||#{keyword}||'%'
AND A.LOCATION LIKE '%'||#{keyword}||'%'
AND A.LIGHT_NO LIKE '%'||#{keyword}||'%'
AND A.TROUBLE_CD = #{trouble_cd}
AND A.PROGRESS_STATUS = #{progress_status}
ORDER BY A.REPAIR_NO DESC

SELECT A.MEMBER_ID AS DATA_CODE, A.MEMBER_NAME AS DATA_CODE_NAME FROM GY_MEMBER A WHERE A.GRADE = '03'
AND A.YEAR = #{searchYear}
ORDER BY A.MEMBER_ID DESC

SELECT LIGHT_NO AS "관리번호", ADDRESS AS "주소", NEW_ADDRESS AS "새주소", POLE_NO AS "인입주", BDJ AS "변대주", SET_YMD AS "설치년도", MAP_X_POS_GL AS "X좌표", MAP_Y_POS_GL AS "Y좌표", F_GY_DATA_CODE_NM('13', LIGHT_TYPE) AS "등종류", F_GY_DATA_CODE_NM('05', LIGHT_GUBUN) AS "등상태", F_GY_DATA_CODE_NM('06', HJ_DONG_CD) AS "행정동", F_GY_DATA_CODE_NM('08', STAND_CD) AS "지지방식", F_GY_DATA_CODE_NM('09', LAMP1_CD) AS "등기구형태", F_GY_DATA_CODE_NM('10', LAMP2_CD) AS "광원종류", F_GY_DATA_CODE_NM('11', LAMP3_CD) AS "광원용량", KEPCO_CUST_NO AS "한전고객번호", F_GY_DATA_CODE_NM('15', ONOFF_CD) AS "점멸기" FROM GY_LIGHT A WHERE 1=1
AND A.LIGHT_TYPE = #{excelLightType}
AND A.HJ_DONG_CD = #{hj_dong_cd}
AND STAND_CD = #{stand_cd}
AND LAMP1_CD = #{lamp1_cd}
AND LAMP2_CD = #{lamp2_cd}
AND LAMP3_CD = #{lamp3_cd}
AND ONOFF_CD = #{onoff_cd}
AND (KEPCO_CUST_NO IS NOT NULL AND KEPCO_CUST_NO != '' AND KEPCO_CUST_NO != '0')
AND (KEPCO_CUST_NO IS NULL OR KEPCO_CUST_NO = '')
ORDER BY LIGHT_NO

SELECT A.REPAIR_NO AS "접수번호" , A.LIGHT_NO AS "관리번호" , F_GY_DATA_CODE_NM('06', A.DONG) AS "행정동" , F_GY_DATA_CODE_NM('13', A.LIGHT_GUBUN) AS "등구분" , COALESCE(CASE WHEN A.REPAIR_GUBUN = '1' THEN '교환' WHEN A.REPAIR_GUBUN = '2' THEN '이설' WHEN A.REPAIR_GUBUN = '3' THEN '철거' WHEN A.REPAIR_GUBUN = '4' THEN '보수' END ,'') AS "작업구분" , COALESCE(B.REPAIR_DESC, '') AS "작업비고" , COALESCE(TO_CHAR(LAST_UPDATE, 'YYYY-MM-DD'), '') AS "최종등록일" FROM GY_LIGHT_REPAIR A LEFT OUTER JOIN GY_LIGHT_REPAIR_PART B ON A.REPAIR_NO=B.REPAIR_NO WHERE 1=1
AND TO_CHAR(A.NOTICE_DATE, 'YYYY-MM-DD') BETWEEN #{sDate3} AND #{eDate3}
AND B.COMPANY_ID = #{company_id2}
ORDER BY A.DONG, A.REPAIR_NO DESC

mapper.update:
UPDATE GY_LIGHT
SET
	OLD_LIGHT_NO = #{old_light_no}
	, HJ_DONG_CD = #{hj_dong_cd}
	, LIGHT_TYPE = #{light_type}
	, MGMT_NO = #{mgmt_no}
	, DOYEP_NO = #{doyep_no}
	, BDJ = #{bdj}
	, POLE_NO = #{pole_no}
	, LIGHT_GUBUN = #{light_gubun}
	, TWIN_LIGHT = #{twin_light}
	, LAMP1_CD = #{lamp1_cd}
	, LAMP1_CD2 = #{lamp1_cd2}
	, LAMP2_CD = #{lamp2_cd}
	, LAMP2_CD2 = #{lamp2_cd2}
	, LAMP3_CD = #{lamp3_cd}
	, LAMP3_CD2 = #{lamp3_cd2}
	, ONOFF_CD = #{onoff_cd}
	, STAND_CD = #{stand_cd}
	, KEPCO_CUST_NO = #{kepco_cust_no}
	, KEPCO_CD = #{kepco_cd}
	, WORK_CD = #{work_cd}
	, SET_YMD = TO_CHAR(TO_DATE(#{set_ymd}, 'YYYY-MM-DD'),'YYYYMMDD')
	, USE_LIGHT = #{use_light}
	, ADDRESS = #{address}
	, NEW_ADDRESS = #{new_address}
	, LAST_UPDATE = now()
WHERE
	LIGHT_NO = #{light_no}

UPDATE GY_LIGHT
SET
	HJ_DONG_CD = #{hj_dong_cd}
	, LIGHT_TYPE = #{light_type}
	, MAP_X_POS_GL = #{mapPosX}
	, MAP_Y_POS_GL = #{mapPosY}
	, POLE_NO = #{pole_no}
	, LAMP2_CD = #{lamp2_cd}
	, LAMP3_CD = #{lamp3_cd}
	, KEPCO_CUST_NO = #{kepco_cust_no}
	, KEPCO_CD = #{kepco_cd}
	, USE_LIGHT = #{use_light}
	, ADDRESS = #{address}
	, NEW_ADDRESS = #{new_address}
	, LAST_UPDATE = now()
WHERE
	LIGHT_NO = #{light_no}

mapper.insert:
INSERT INTO GY_LIGHT (
	LIGHT_NO, LIGHT_TYPE, HJ_DONG_CD, LIGHT_GUBUN, LAMP2_CD
	, LAMP3_CD, POLE_NO, KEPCO_CUST_NO, KEPCO_CD, USE_LIGHT
	, MAP_X_POS_GL, MAP_Y_POS_GL, ADDRESS, NEW_ADDRESS
)
VALUES (
	#{light_no}, #{light_type}, #{hj_dong_cd}, '1', #{lamp2_cd}
	, #{lamp3_cd}, #{pole_no}, #{kepco_cust_no}, #{kepco_cd}, #{use_light}
	, #{mapPosX}, #{mapPosY}, #{address}, #{new_address}
)
